import React, { Component } from 'react'
import "./App.css";

class ListProducts extends Component {

  state = { reset: "" }

  componentDidMount = async () => {
  };

  claimNft = async (tokenId) => {
    await this.props.airdropInstance.methods.claimNft(tokenId).send({from: this.props.account});
    window.location.reload()
  } 

  render() {
    if (this.props.nftsA === "0") {
      return(
        <p id="no-nfts">No NFTs available for airdrop yet.</p>
      );
    } else {
      return (
        <div className="App">
          <h1>Airdrop</h1>
          <p class="nft-info">{this.props.nftsA} has been listed for airdrop</p>
          <div class="nfts">
            {this.props.nfts.map((nft, key) => {
              if (nft.purpose == 0) {
                return (
                  <div class="nft">
                    <div class="white-box">
                      <h2>{nft.title}</h2>
                      <h3 class="nft-info">Id: #{nft.id}</h3>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">{nft.description}</p>
                      <p class="nft-info">Price: {nft.price/10**18} ether</p>
                      {nft.status == 0 ? <button onClick={() => this.claimNft(nft.id)} class="purchase">Claim</button> : <button  class="purchase-disabled" disabled>Claimed</button>}
                    </div>
                  </div>
                );
              }
            })}
          </div>
        </div>
      );
    }

  }
}

export default ListProducts;
