import React, { Component } from 'react'
import "./App.css";

class ListProducts extends Component {

  state = {  }

  componentDidMount = async () => {
  };

  claimNft = async (tokenId) => {
    await this.props.airdropInstance.methods.claimNft(tokenId).send({from: this.props.account});
  } 

  render() {
    if (this.props.nftsA === "0") {
      return(
        <p id="no-nfts">No NFTs available for airdrop yet.</p>
      );
    } else {
      return (
        <div className="App">
          <h1>Airdrop</h1>
          {this.props.nfts.map((nft, key) => {
            if (nft.purpose == 0) {
              return (
                <div class="nft">
                  <h2>{nft.title}</h2>
                  <p class="nft-info">Id: #{nft.id}</p>
                  <img id="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                  <p class="nft-info" id="desc">{nft.description}</p>
                  <p class="nft-info">Price: {nft.price} ether</p>
                  <p class="nft-info">Status: {nft.status == 0 ? "Unclaimed" : "Claimed"}</p>
                  {nft.status == 0 ? <button onClick={() => this.claimNft(nft.id)} class="purchase">Claim</button> : <button  class="purchase"disabled>Claimed</button>}
                </div>
              );
            }
          })}
        </div>
      );
    }

  }
}

export default ListProducts;
