import React from "../node_modules/react";
import "./App.css";
import "./index.css";
import Main from "./Main";
import Airdrop from "./Airdrop";
import Navbar from "./Navbar";
import MyNFTs from "./MyNFTs";
import NFTAirdrop from "./contracts/NFTAirdrop.json";
import getWeb3 from "./getWeb3";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
/**
 * @App Build Decentralized Art Market using ERC-721
 * @Util initail App build main class
 * @Book Learn Ethereum
 * @author brian wu
 */
const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' }) // leaving out the arguments will default to these values

class App extends React.Component {

  state = { web3: null, account: "", networkId: null, airdropInstance: null, ipfs: null, nfts: [], airdropEnd: null }

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      let account = await window.ethereum.request({ method: 'eth_accounts' });
      account = account[0];

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetworkAirdrop = NFTAirdrop.networks[networkId];
      const airdropInstance = new web3.eth.Contract(
        NFTAirdrop.abi,
        deployedNetworkAirdrop && deployedNetworkAirdrop.address,
      );

      let airdropEnd = await airdropInstance.methods.airdropEnd().call();
      console.log(airdropEnd)

      let nfts = await airdropInstance.methods.allNfts().call();
      console.log(nfts)

      this.setState({ web3, account, networkId, airdropInstance, ipfs, nfts, airdropEnd })

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Please connect your metamask and switch to the binance test chain`,
      );
      console.error(error)

    }
  }

  render() {
    return (
      <Router>
        <Switch>
          <div>
            <Navbar account={this.state.account}/>
            <Route exact path='/'>
                <Main web3={this.state.web3} account={this.state.account} networkId={this.state.networkdId} airdropInstance={this.state.airdropInstance} ipfs={this.state.ipfs} nfts={this.state.nfts} airdropEnd={this.state.airdropEnd}/>
            </Route>
            <Route exact path='/airdrop'>
                <Airdrop web3={this.state.web3} account={this.state.account} networkId={this.state.networkdId} airdropInstance={this.state.airdropInstance} ipfs={this.state.ipfs} nfts={this.state.nfts} airdropEnd={this.state.airdropEnd}/>
            </Route>
            <Route exact path='/mynfts'>
                <MyNFTs web3={this.state.web3} account={this.state.account} networkId={this.state.networkdId} airdropInstance={this.state.airdropInstance} ipfs={this.state.ipfs} nfts={this.state.nfts} airdropEnd={this.state.airdropEnd}/>
            </Route>
          </div>
        </Switch>
      </Router>
    );
  }
}
export default App;
