import React, { Component } from 'react'
import "./App.css";

class ListProducts extends Component {

  state = { reset: "" }

  componentDidMount = async () => {
  };

  claimNft = async (tokenId) => {
    await this.props.airdropInstance.methods.claimNft(tokenId).send({from: this.props.account});
    window.location.reload()
  } 

  render() {
    if (this.props.nftsA === "0") {
      return(
        <p id="no-nfts">No NFTs available for airdrop yet.</p>
      );
    } else {
      return (
        <div className="App">
          <h1>Airdrop</h1>
          <div class="nfts">
                <div class="nft">
                  <div class="white-box">
                    <h2>CryptoPunk</h2>
                    <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmZoV3cFySq57pckUHHGvVk42VimhCvUH4tyNsUTgTEcYD`} alt="IPFS Upload"/>
                    <p class="nft-info" id="desc">A cool cryptopunk</p>
                    <p class="nft-info">Price: {this.props.price/10**18} ether</p>
                  </div>
                </div>
                <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmUnbEDDCM2LqRGLdTq4KgP3QCqfAQkkrezoAMGongagLe`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">A cool cryptopunk</p>
                      <p class="nft-info">Price: {this.props.price/10**18} ether</p>
                    </div>
                  </div>
                  <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmWfHV3gpWsAc5arTXjvQ7NmZskqYwT2j4vCQSCWvzZFpA`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">A cool cryptopunk</p>
                      <p class="nft-info">Price: {this.props.price/10**18} ether</p>
                    </div>
                  </div>
          </div>
        </div>
      );
    }

  }
}

export default ListProducts;
