import React, { Component } from "react";
import "./App.css";


class MyNFTs extends Component {

  state = {  };

  componentDidMount = async () => {
  };



  render() {
    return (
        <div className="App">
        <h1>My NFTs</h1>
        <p id="nft-info">You can claim these at the end of the presale which is: {this.props.airdropEnd}</p>
        <div class="nfts">
          {this.props.nfts.map((nft, key) => {
              if (nft.owner.toLowerCase() == this.props.account.toLowerCase()) {
                return (
                    <div class="nft">
                      <h2>{nft.title}</h2>
                      <p class="nft-info">Id: #{nft.id}</p>
                      <img id="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">{nft.description}</p>
                      <p class="nft-info">Price: {nft.price} ether</p>
                      <p class="nft-info">Status: {nft.status}</p>
                      <p class="nft-info">{nft.claimed == 1 ? "Claimed" : <button class="purchase">Claim</button>}</p>
                    </div>
                  );
              }
          })}
        </div>
      </div>
    );
  }
}

export default MyNFTs;
