import React, { Component } from "react";

import "./App.css";


class Main extends Component {

  state = {};

  componentDidMount = async () => {
  };

  buyNft = async (tokenId, tokenPrice) => {
    await this.props.airdropInstance.methods.buyNft(tokenId).send({from: this.props.account, value: tokenPrice});
    window.location.reload()
  }

  render() {
    if (this.props.nftsS === "0") {
      return(
        <p id="no-nfts">No NFTs up for sale yet.</p>
      );
    } else {
      return (
        <div className="App">
          <h1>Sale</h1>
          <p class="nft-info">{this.props.nftsS} has been listed for sale</p>
          <div class="nfts">
                <div class="nft">
                  <div class="white-box">
                    <h2>CryptoPunk</h2>
                    <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                    <p class="nft-info" id="desc">{nft.description}</p>
                    <p class="nft-info">Price: {this.props.price/10**18} ether</p>
                  </div>
                </div>
                <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">{nft.description}</p>
                      <p class="nft-info">Price: {this.props.price/10**18} ether</p>
                    </div>
                  </div>
                  <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">{nft.description}</p>
                      <p class="nft-info">Price: {this.props.price/10**18} ether</p>
                    </div>
                  </div>
          </div>
        </div>
      );
    }
  }
}

export default Main;
