import React, { Component } from "react";

import "./App.css";


class Main extends Component {

  state = {  };

  componentDidMount = async () => {
  };


  render() {
    console.log(this.props.nfts)
    if (this.props.nfts === []) {
      return(
        <p>No NFTs listed yet.</p>
      );
    } else {
      return (
        <div className="App">
          <h1>Sale</h1>
          <p class="nft-info">dwa{this.props.nftS}</p>
          <div class="nfts">
            {this.props.nfts.map((nft, key) => {
              if (nft.purpose == 1) {
                return (
                  <div class="nft">
                    <h2>{nft.title}</h2>
                    <p class="nft-info">Id: #{nft.id}</p>
                    <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/${nft.image}`} alt="IPFS Upload"/>
                    <p class="nft-info" id="desc">{nft.description}</p>
                    <p class="nft-info">Price: {nft.price} ether</p>
                    <p class="nft-info">Status: {nft.status}</p>
                    <button class="purchase">Buy</button>
                  </div>
                );
              }
            })}
          </div>
          <img id="thunder-man" src="https://tsunamiavax.com/assets/images/thunder-men.png"></img>
        </div>
      );
    }
  }
}

export default Main;
