import React, { Component } from "react";
import ProductBuyContract from "./contracts/ProductBuy.json";
import TestToken from "./contracts/TestToken.json";
import getWeb3 from "./getWeb3";

import "./App.css";


class Main extends Component {

  state = { action: "", products: null, web3: null, account: null, contract: null, token: null, tokenName: null, approved: false, product: null, statuses: null, decimals: null, balance: null };

  componentDidMount = async () => {
  };


  render() {
    let content;
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contracts...</div>;
    }
    return (
      <div className="App">
        <h1>Product Marketplace</h1>
        <p>Your {this.state.tokenName} balance: {this.state.balance/10**this.state.decimals}</p>
        <div id="products">
          {content}
        </div>
      </div>
    );
  }
}

export default Main;
