import React, { Component } from "react";

import "./App.css";


class Main extends Component {

  state = {};



  componentDidMount = async () => {
    this.images = [
      {
        "path": "Attachment_1631486053 (3).png",
        "hash": "QmYy72dpMeq56PjRAcjNdwqumaZBobCTeHGMrPVpaTmwdQ",
        "size": 110944
      },
      {
        "path": "Attachment_1631486053 (1).png",
        "hash": "Qmc2me9QwSgcYqfutfF36cv1iy3tFAt1YEkR422ySbgVh7",
        "size": 208698
      },
      {
        "path": "Attachment_1631486053.png",
        "hash": "QmeJpX8sHsogZJiKgKmnUbw9zrTNdGszPbP2Y8M1rAr7Zh",
        "size": 168073
      },
      {
        "path": "Attachment_1631486053 (2).png",
        "hash": "QmcCrKTSYLhCbAk4EBTVjKj9a6SiTBrPoPDSH8VWbiSeaC",
        "size": 225720
      },
      {
        "path": "Attachment_1631486053 (4).png",
        "hash": "QmcmDin4ECqiBx2BA5TExvuAUShSB7BYwTQg2wAfjwdYxt",
        "size": 212275
      }
    ]
  };

  buyNft = async () => {
    let min = Math.ceil(0);
    let max = Math.floor(this.images.length);
    let imageIndex = Math.floor(Math.random() * (max - min) + min);
    let randomImage = this.images[imageIndex]["hash"]
    document.querySelector('#sair').innerHTML = 'Buying...';
    try {
      await this.props.airdropInstance.methods.mint(1, "CryptoPunk#"+this.props.nfts.length, randomImage).send({from: this.props.account, value: this.props.price});
      document.querySelector('#sair').innerHTML = 'Succesfully Bought';
      window.location.replace("/mynfts")
    } catch (error) {
      alert("Transaction Rejected")
      document.querySelector('#sair').innerHTML = 'Buy';
    }
  }

  render() {
      return (
        <div className="App">
          <h1>Sale</h1>
          <p class="nft-info">Mint tokens from our sale! Every NFT has a price of {this.props.price/10**18} ether.</p>
          <p class="nft-info">NFTs can only be bought from {this.props.airdropStart} to {this.props.airdropEnd}</p>
          <p class="nft-info">Number of NFTs left to buy: {this.props.maxSale - this.props.nftsS}</p>
          <button id="sair" class="mint" onClick={() => this.buyNft()}>Buy</button>
          <div class="nfts">
                <div class="nft">
                  <div class="white-box">
                    <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmYy72dpMeq56PjRAcjNdwqumaZBobCTeHGMrPVpaTmwdQ`} alt="IPFS Upload"/>
                  </div>
                </div>
                <div class="nft">
                    <div class="white-box">
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/Qmc2me9QwSgcYqfutfF36cv1iy3tFAt1YEkR422ySbgVh7`} alt="IPFS Upload"/>
                    </div>
                  </div>
                  <div class="nft">
                    <div class="white-box">
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmeJpX8sHsogZJiKgKmnUbw9zrTNdGszPbP2Y8M1rAr7Zh`} alt="IPFS Upload"/>
                    </div>
                  </div>
          </div>
        </div>
      );
  }
}

export default Main;
