import React, { Component } from "react";
import ProductBuyContract from "./contracts/ProductBuy.json";
import TestToken from "./contracts/TestToken.json";
import getWeb3 from "./getWeb3";
import Navbar from "./Navbar.js";

import "./App.css";

class App extends Component {
  state = { products: null, web3: null, accounts: null, contract: null, token: null, tokenName: null, approved: false };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetworkProduct = ProductBuyContract.networks[networkId];
      const instance = new web3.eth.Contract(
        ProductBuyContract.abi,
        deployedNetworkProduct && deployedNetworkProduct.address,
      );

      let tokenAddress = await instance.methods.token().call();

      const deployedNetworkToken = TestToken.networks[networkId];
      const token = new web3.eth.Contract(
        TestToken.abi,
        deployedNetworkToken && deployedNetworkToken.address,
      );

      this.product = deployedNetworkToken.address;

      let products = await instance.methods.viewProducts().call();

      let tokenName = await token.methods.name().call();

      let tokenSymbol = await token.methods.symbol().call();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance, products, token,tokenName, tokenSymbol });

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  handleApprove = async (price) => {
    await this.state.token.methods.approve(this.product, price).send({from: this.state.accounts[0]});
    this.setState({approved: true})
  }

  handleBuy = async (id) => {
    await this.state.contract.methods.buyProduct(id).send({from: this.state.accounts[0]});
  }

  render() {
    let content;
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    } else if (!this.state.products) {
      content = <p>Nothing</p>
    }
    return (
      <div className="App">
        <Navbar account={this.state.accounts[0]}/>
        <h1>Product Marketplace</h1>
        <div id="products">
          {content}
        </div>
      </div>
    );
  }
}

export default App;
