import React from "../node_modules/react";
import "./App.css";
import "./index.css";
import Main from "./Main";
import Airdrop from "./Airdrop";
import Navbar from "./Navbar";
import NFTAirdrop from "./contracts/NFTAirdrop.json";
import getWeb3 from "./getWeb3";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
/**
 * @App Build Decentralized Art Market using ERC-721
 * @Util initail App build main class
 * @Book Learn Ethereum
 * @author brian wu
 */
class App extends React.Component {

  state = { account: "" }

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      let account = await window.ethereum.request({ method: 'eth_accounts' });
      account = account[0];

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetworkAirdrop = NFTAirdrop.networks[networkId];
      const airdropInstance = new web3.eth.Contract(
        NFTAirdrop.abi,
        deployedNetworkAirdrop && deployedNetworkAirdrop.address,
      );

      this.setState({web3, account, networkId, airdropInstance})

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Please connect your metamask and switch to the binance test chain`,
      );

    }
  }

  render() {
    return (
      <Router>
        <Switch>
          <div>
            <Navbar account={this.state.account}/>
            <Route exact path='/'>
                <Main web3={this.state.web3} account={this.state.account} networkId={this.state.networkdId}/>
            </Route>
            <Route exact path='/airdrop'>
                <Airdrop />
            </Route>
          </div>
        </Switch>
      </Router>
    );
  }
}
export default App;
