import React, { Component } from "react";

import "./App.css";


class Main extends Component {

  state = {};



  componentDidMount = async () => {
    this.images = ["QmZoV3cFySq57pckUHHGvVk42VimhCvUH4tyNsUTgTEcYD", 
              "QmUnbEDDCM2LqRGLdTq4KgP3QCqfAQkkrezoAMGongagLe", 
              "QmWfHV3gpWsAc5arTXjvQ7NmZskqYwT2j4vCQSCWvzZFpA"]
  };

  buyNft = async () => {
    let min = Math.ceil(0);
    let max = Math.floor(this.images.length);
    let imageIndex = Math.floor(Math.random() * (max - min) + min);
    let randomImage = this.images[imageIndex]
    document.querySelector('#sair').innerHTML = 'Buying...';
    try {
      await this.props.airdropInstance.methods.mint(1, "CryptoPunk#"+this.props.nfts.length, randomImage).send({from: this.props.account, value: this.props.price});
      document.querySelector('#sair').innerHTML = 'Succesfully Bought';
      window.location.replace("/mynfts")
    } catch (error) {
      alert("Transaction Rejected")
      document.querySelector('#sair').innerHTML = 'Buy';
    }
  }

  render() {
      return (
        <div className="App">
          <h1>Sale</h1>
          <p class="nft-info">Mint tokens from our sale! Every NFT has a price of {this.props.price/10**18} ether.</p>
          <p class="nft-info">NFTs can only be claimed from {this.props.airdropStart} to {this.props.airdropEnd}</p>
          <button id="sair" class="mint" onClick={() => this.buyNft()}>Buy</button>
          <div class="nfts">
                <div class="nft">
                  <div class="white-box">
                    <h2>CryptoPunk</h2>
                    <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmZoV3cFySq57pckUHHGvVk42VimhCvUH4tyNsUTgTEcYD`} alt="IPFS Upload"/>
                    <p class="nft-info" id="desc">A cool cryptopunk</p>

                  </div>
                </div>
                <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmUnbEDDCM2LqRGLdTq4KgP3QCqfAQkkrezoAMGongagLe`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">A cool cryptopunk</p>
                    </div>
                  </div>
                  <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmWfHV3gpWsAc5arTXjvQ7NmZskqYwT2j4vCQSCWvzZFpA`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">A cool cryptopunk</p>
                    </div>
                  </div>
          </div>
        </div>
      );
  }
}

export default Main;
