import React, { Component } from 'react'
import Navbar from "./Navbar.js";
import getWeb3 from "./getWeb3";
import "./App.css";

class ListProducts extends Component {

    state = { account: "" }

    componentDidMount = async () => {
        try {
          // Get network provider and web3 instance.
          const web3 = await getWeb3();

          const account = await window.ethereum.request({ method: 'eth_accounts' });
          console.log(account[0])

          // Set web3, accounts, and contract to the state, and then proceed with an
          // example of interacting with the contract's methods.
          this.setState({ web3, account: account[0] });

        } catch (error) {
          // Catch any errors for any of the above operations.
          alert(
            `Failed to load web3, accounts, or contract. Check console for details.`,
          );
          console.error(error);
        }
    };    


  render() {
    return (
        <div className="App">
            <Navbar account={this.state.account}/>
            <h1>List Products</h1>
            <form class="form-style-4" action="" method="post">
                <label for="field1">
                    <span>Enter Your Name</span><input type="text" name="field1" required="true" />
                </label>
                <label for="field2">
                    <span>Email Address</span><input type="email" name="field2" required="true" />
                </label>
                <label for="field3">
                    <span>Short Subject</span><input type="text" name="field3" required="true" />
                </label>
                <label>
                    <span></span><input type="submit" value="List Product" />
                </label>
            </form>
        </div>
    );
  }
}

export default ListProducts;
