import React, { Component } from 'react'
import "./App.css";

class ListProducts extends Component {

  state = { reset: "" }

  componentDidMount = async () => {
    this.images = ["QmZoV3cFySq57pckUHHGvVk42VimhCvUH4tyNsUTgTEcYD", 
      "QmUnbEDDCM2LqRGLdTq4KgP3QCqfAQkkrezoAMGongagLe", 
      "QmWfHV3gpWsAc5arTXjvQ7NmZskqYwT2j4vCQSCWvzZFpA"]
  };

  claimNft = async () => {
    let min = Math.ceil(0);
    let max = Math.floor(this.images.length);
    let imageIndex = Math.floor(Math.random() * (max - min) + min);
    let randomImage = this.images[imageIndex]
    document.querySelector('#mair').innerHTML = 'Claiming...';
    try {
      await this.props.airdropInstance.methods.mint(0, "CryptoPunk#"+this.props.nfts.length, randomImage).send({from: this.props.account});
      document.querySelector('#mair').innerHTML = 'Successfully Claimed';
      window.location.replace("/mynfts")
    } catch (error) {
      alert("Transaction Rejected")
      document.querySelector('#mair').innerHTML = 'Claim Airdrop';
    }
  } 

  render() {
      return (
        <div className="App">
          <h1>Airdrop</h1>
          <p class="nft-info">Mint tokens from our airdrop! Claiming is free! But there are a limited amount of NFTs for airdrop</p>
          <p class="nft-info">NFTs can only be claimed from {this.props.airdropStart} to {this.props.airdropEnd}</p>
          <p class="nft-info">Number of NFTs left to claim: {this.props.maxSale - this.props.nftsS}</p>
          <button id="mair" class="mint" onClick={() => this.claimNft()}>Claim Airdrop</button>
          <div class="nfts">
                <div class="nft">
                  <div class="white-box">
                    <h2>CryptoPunk</h2>
                    <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmZoV3cFySq57pckUHHGvVk42VimhCvUH4tyNsUTgTEcYD`} alt="IPFS Upload"/>
                    <p class="nft-info" id="desc">A cool cryptopunk</p>
                  </div>
                </div>
                <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmUnbEDDCM2LqRGLdTq4KgP3QCqfAQkkrezoAMGongagLe`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">A cool cryptopunk</p>
                    </div>
                  </div>
                  <div class="nft">
                    <div class="white-box">
                      <h2>CryptoPunk</h2>
                      <img class="ipfs-image" src={`https://ipfs.infura.io/ipfs/QmWfHV3gpWsAc5arTXjvQ7NmZskqYwT2j4vCQSCWvzZFpA`} alt="IPFS Upload"/>
                      <p class="nft-info" id="desc">A cool cryptopunk</p>
                    </div>
                  </div>
          </div>
        </div>
      );
  }
}

export default ListProducts;
