import React from "../node_modules/react";
import "./App.css";
import "./index.css";
import Main from "./Main";
import Navbar from "./Navbar";
import ProductBuyContract from "./contracts/ProductBuy.json";
import TestToken from "./contracts/TestToken.json";
import getWeb3 from "./getWeb3";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
/**
 * @App Build Decentralized Art Market using ERC-721
 * @Util initail App build main class
 * @Book Learn Ethereum
 * @author brian wu
 */
class App extends React.Component {

  state = { account: "" }

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const account = await window.ethereum.request({ method: 'eth_accounts' });
      console.log(account[0])

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetworkAirdrop = ProductBuyContract.networks[networkId];
      const instance = new web3.eth.Contract(
        ProductBuyContract.abi,
        deployedNetworkProduct && deployedNetworkProduct.address,
      );

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Please connect your metamask and switch to the binance test chain`,
      );

    }
  }

  render() {
    return (
      <Router>
        <Switch>
          <div>
            <Navbar account={this.state.account}/>
            <Route exact path='/'>
                <Main />
            </Route>
            <Route exact path='/airdrop'>
                <ListProducts />
            </Route>
          </div>
        </Switch>
      </Router>
    );
  }
}
export default App;
